mapper.update:
update os_member 
set remove_user_date=sysdate
where id=#{memberVO.id}

UPDATE os_member
nickname=#{nickName}, address=#{address}, phone=#{phone}, email=#{email}, name=#{name}, profile=#{profile}
WHERE id=#{id}

UPDATE os_artist 

	sns=#{sns},
	account=#{account},
	artist_info=#{artistInfo}

WHERE id=#{memberVO.id}

mapper.select:
select count(*)
from  os_follow
where id=#{memberVO.id}

SELECT COUNT(*) FROM os_follow 
WHERE following_id=#{followingId} 
AND id=#{memberVO.id}

select count(*) 
from os_event
where id=#{value}
